<script>
    const navigationDot = document.querySelectorAll(".navigationDot");
    const thunder = document.querySelector(".thunder");
    const animRed = document.querySelectorAll(".animRed");
    const animBlue = document.querySelectorAll(".animBlue");
    const animPurple = document.querySelectorAll(".animPurple");
    const sequenceList = [animRed, animBlue, animPurple];
    const colors = ["#F20F0F", "#019BE5", "#04cc72"];

    let sequenceCounter = 0;
    const slider = setInterval(() => {
        const oldSequenceCounter = sequenceCounter - 1 == -1 ? 2 : sequenceCounter - 1;
        const newSequence = sequenceList[sequenceCounter];
        const oldSequence = sequenceList[oldSequenceCounter];

        newSequence[0].classList.remove("opacity-0");
        oldSequence[0].classList.add("opacity-0");
        newSequence[1].classList.remove("opacity-0");
        oldSequence[1].classList.add("opacity-0");
        navigationDot[sequenceCounter].classList.remove("opacity-10");
        navigationDot[oldSequenceCounter].classList.add("opacity-10");

        thunder.style.fill = colors[sequenceCounter];
        sequenceCounter++;
        //Go To Anim Sequence 1
        if (sequenceCounter == 3) sequenceCounter = 0;
    }, 5000);
</script>


